import React from 'react';
import './GameBoard.css';

import GameSquare from '../GameSquare/GameSquare';

const GameBoard = ({gameState}) => {
  return (
    <div className="gameBoard">
      <div className="row">
        {gameState[0].map((i, k) => (
          <GameSquare key={k} isOn={i !== 0} id/>
        ))}
      </div>
      <div className="row">
        {gameState[1].map((i, k) => (
          <GameSquare key={k} isOn={i !== 0}/>
          ))}
      </div>
      <div className="row">
        {gameState[2].map((i, k) => (
          <GameSquare key={k} isOn={i !== 0}/>
          ))}
      </div>
      <div className="row">
        {gameState[3].map((i, k) => (
          <GameSquare key={k} isOn={i !== 0}/>
        ))}
      </div>
      <div className="row">
        {gameState[4].map((i, k) => (
          <GameSquare key={k} isOn={i !== 0}/>
        ))}
      </div>
    </div>
  );
};

export default GameBoard;
